mapper.select:
SELECT * FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND PASSWORD = #{password}
AND STATUS = 'y'


SELECT MEMBER_ID 
FROM MEMBER
WHERE NAME = #{name}
AND PHONE = #{phone}
AND STATUS = 'y'


SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password}
WHERE MEMBER_id = #{memberId}
AND EMAIL = #{email}

UPDATE MEMBER
SET PASSWORD = #{password}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET PASSWORD = #{password}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET DELIVERY_STATUS = #{deliveryStatus} 
WHERE MEMBER_NO = #{memberNo}

mapper.select:
