mapper.update:
UPDATE partner_Doctor
SET COMMENTSCNT = COMMENTSCNT +
#{amount}
WHERE
member_NO=#{cmainNo}

mapper.select:
 SELECT PROFILE_EDUCATION
	 FROM PARTNER_DOCTOR 
	 WHERE MEMBER_NO = #{memberNo}

mapper.delete:
DELETE LIKES
WHERE MEMBER_NO = #{memberNo} 
AND CATEGORY='DOC' 
AND LIKE_MAIN_NO = #{likeMainNo}
