mapper.update:
UPDATE PATIENTS
	SET
		PRE_SELF_AX=#{preSelfAx}
	WHERE MEMBER_NO=#{memberNo}

mapper.select:
SELECT *
FROM( SELECT /*+INDEX_DESC(PATIENTS PATIENTS_PK) */
ROWNUM RN, MEMBER_NO, PAST_HX, PRE_SELF_AX, TOPIC, MED_DELIVERY, REG_DATE
FROM PATIENTS
where



		ROWNUM <= #{pageNum} * #{amount}				
		)
WHERE RN > (#{pageNum}-1)* #{amount}

SELECT *
FROM( SELECT /*+INDEX_DESC(PARTNER_DOCTOR PARTNER_DOCTOR_PK) */
ROWNUM RN, MEMBER_NO, PROFILE_EDUCATION, CLINIC_INFO, CLINIC_PHONE, CLINIC_EMAIL, REG_DATE
FROM PARTNER_DOCTOR
WHERE	



		ROWNUM <= #{pageNum} * #{amount}				
		)
WHERE RN > (#{pageNum}-1)* #{amount}

SELECT *
FROM( SELECT /*+INDEX_DESC(PARTNER_PHARMACY PARTNER_PHARMACY_PK) */
ROWNUM RN, MEMBER_NO, PHARMACY_INFO, PHARMACY_CONTACT, 
PHARMACY_EMAIL, DELIVERY_AREA, REG_DATE
FROM PARTNER_PHARMACY
WHERE	



		ROWNUM <= #{pageNum} * #{amount}				
		)
WHERE RN > (#{pageNum}-1)* #{amount}

SELECT *
FROM( SELECT /*+INDEX_DESC(BOOKING BOOKING_PK) */
ROWNUM RN, B.BOOKING_NO, B.PT_NO, B.DOC_NO, B.BOOKING_DATE, B.PRICE,
B.BOOKING_STATUS, B.CONSULT_DATE, B.CONSULT_TIME, M.NAME
FROM BOOKING B JOIN MEMBER M
ON B.PT_NO=M.MEMBER_NO
WHERE B.CONSULT_DATE=TO_DATE(SYSDATE) AND M.MEMBER_NO=#{memberNo} AND

		ROWNUM <= #{cri.pageNum} * #{cri.amount}				
		)
WHERE RN > (#{cri.pageNum}-1)* #{cri.amount}

SELECT *
FROM( SELECT /*+INDEX_DESC(BOOKING BOOKING_PK) */
ROWNUM RN, B.BOOKING_NO, B.PT_NO, B.DOC_NO, B.BOOKING_DATE, B.PRICE,
B.BOOKING_STATUS, B.CONSULT_DATE, B.CONSULT_TIME, M.NAME
FROM BOOKING B JOIN MEMBER M
ON B.PT_NO = M.MEMBER_NO
WHERE B.BOOKING_STATUS = 'y' AND M.MEMBER_NO=#{memberNo} AND

		ROWNUM <= #{cri.pageNum} * #{cri.amount}				
		)
WHERE RN > (#{cri.pageNum}-1)* #{cri.amount}

		SELECT *
		FROM( SELECT /*+INDEX_DESC(COMMENTS COMMENTS_PK) */
		ROWNUM RN, C.CMAIN_CATEGORY, C.CMAIN_NO, C.COMMENT_WRITER_NO, 
		C.CONTENTS, C.REVIEW_POINT, C.REG_DATE, M.NICKNAME
		FROM COMMENTS C JOIN MEMBER M
		ON (C.COMMENT_WRITER_NO = M.MEMBER_NO)
        WHERE M.MEMBER_NO=#{memberNo} AND

				ROWNUM <= #{cri.pageNum} * #{cri.amount}				
				)
		WHERE RN > (#{cri.pageNum}-1)* #{cri.amount}

mapper.update:
UPDATE MEMBER
	SET
		EMAIL=#{email},
		PHONE=#{phone}
WHERE MEMBER_NO=#{memberNo}
