mapper.insert:

	SELECT SEQ_PATIENTS.NEXTVAL FROM DUAL

INSERT INTO PATIENTS(MEMBER_NO
					,PAST_HX
					,PRE_SELF_AX
					,TOPIC
					,MED_DELIVERY
					,REG_DATE
					,EDIT_DATE)
VALUES(#{memberNo}
		,#{pastHx}
		,#{preSelfAx}
		,#{topic}
		,#{medDelivery}
		,#{regDate}
		,#{editDate})

mapper.update:
UPDATE PATIENTS
SET TOPIC = #{topic}
	,MED_DELIVERY=#{medDelivery}
WHERE MEMBER_NO=#{memberNo}
