mapper.select:

		SELECT POST_NO, 
			   MEMBER_NO, 
			   POST_DATE, 
			   CONTENTS, 
			   POST_LIKE, 
			   POST_COLOR, ( SELECT 1 FROM REPORT
					         WHERE REPORT_MNO = #{reportMno}
					         AND POST_NO = PK.POST_NO
				      		) REPORT_MNO,       (   SELECT 1 FROM LIKES
										            WHERE MEMBER_NO = #{reportMno}
										            AND LIKE_MAIN_NO = PK.POST_NO
										         ) LIKES_NO										       
		FROM ( SELECT
                  	 		ROWNUM RN, 
                  	 		POST_NO, 
                  	 		MEMBER_NO, 
                  	 		POST_DATE, 
                  	 		CONTENTS, 
                  	 		POST_LIKE, 
      	 					POST_COLOR, 
      	 					(   SELECT 1 FROM REPORT
                                 WHERE REPORT_MNO = #{reportMno}
                                 AND POST_NO = p.POST_NO
                             ) REPORT_MNO, 
                            ( SELECT 1 FROM LIKES
                                WHERE MEMBER_NO = #{reportMno}
                                AND LIKE_MAIN_NO = p.POST_NO
                             ) LIKES_NO
	            	FROM POST p
	                WHERE ROWNUM <= #{pageNum} * #{amount}
	                ORDER BY post_no desc
      			) PK
      WHERE RN > (( #{pageNum} - 1 ) * #{amount} )

	SELECT post.*, (
		SELECT 1 FROM REPORT
		WHERE REPORT_MNO = #{reportMno}
		AND POST_NO = POST.POST_NO
	) REPORT_MNO,
      (
       SELECT 1 FROM LIKES
         WHERE MEMBER_NO = #{reportMno}
         AND LIKE_MAIN_NO = POST.POST_NO
      ) LIKES_NO
	FROM POST
	ORDER BY DBMS_RANDOM.RANDOM()

mapper.update:
UPDATE POST SET 
	POST_LIKE= POST_LIKE + 1 
	WHERE POST_NO = #{postNo}

UPDATE POST SET 
	POST_LIKE= POST_LIKE - 1 
	WHERE POST_NO = #{postNo}
