mapper.select:
SELECT REPORT_NO FROM REPORT
WHERE REPORT_MNO = #{reportMNo}
AND POST_NO = #{postNo}

SELECT A.REPORT_NO AS REPORT_NO, A.POST_NO AS POST_NO, B.MEMBER_NO AS MEMBER_NO, B.MEMBER_ID AS RMEMBER_ID, A.REPORT_MNO AS REPORT_MNO, A.MEMBER_ID AS REPORT_ID, A.REPORT_DATE AS REPORT_DATE, A.CHECK_DATE AS CHECK_DATE, A.REPORT_RESULT AS REPORT_RESULT FROM ( SELECT M.MEMBER_ID , R.POST_NO, R.REPORT_NO, R.REPORT_DATE, R.CHECK_DATE, R.REPORT_RESULT, R.REPORT_MNO FROM REPORT R JOIN MEMBER M ON (R.REPORT_MNO = M.MEMBER_NO))A JOIN ( SELECT M.MEMBER_ID, R.MEMBER_NO, R.POST_NO, R.REPORT_NO FROM REPORT R JOIN MEMBER M ON (R.MEMBER_NO = M.MEMBER_NO))B ON (B.REPORT_NO = A.REPORT_NO)
WHERE A.REPORT_NO = ${reportNo}
ORDER BY A.REPORT_NO ASC
